import React, { useState } from "react";
import MusicControl from "./MusicControl";
import { button, Typography } from "@material-ui/core";
/*
1. Display a calculator
  - Show a display window
  - Show all buttons

2. Capture input from said calculator
2.a. Update the display from input
2.b. Process the input

*/

function App() {
  const [inputs, setInputs] = useState("");
  const [storedInput, setStoredInput] = useState("");
  const [storedOperator, setStoredOperator] = useState("");
  const [history, setHisotry] = useState([]);
  var calculatedValue = "";
  // const songURL = "./assets/SD.mp3";
  function updateInput(event) {
    const { value } = event.currentTarget; //event.currenTarget for Material UI buttons
    setInputs(inputs + value);
  }

  function clearInput(event) {
    setInputs("");
    setStoredInput("");
    setStoredOperator("");
  }

  function updateOperator(event) {
    const { value } = event.target;
    !(inputs === "") && setStoredInput(inputs); //Stores the first number if user had entered a number
    setStoredOperator(value);
    setInputs("");
  }

  function decimalCount() {
    if (!inputs.includes(".")) {
      setInputs(inputs + ".");
    }
  }

  //what if operation was pressed before?
  function clearEntry(event) {
    setInputs(inputs.substr(0, inputs.length - 1));
  }

  function updateCalculate() {
    switch (storedOperator) {
      case "/":
        calculatedValue = (
          parseFloat(storedInput, 10) / parseFloat(inputs, 10)
        ).toString();
        break;
      case "*":
        calculatedValue = (
          parseFloat(storedInput, 10) * parseFloat(inputs, 10)
        ).toString();
        break;
      case "-":
        calculatedValue = (
          parseFloat(storedInput, 10) - parseFloat(inputs, 10)
        ).toString();
        break;
      case "+":
        calculatedValue = (
          parseFloat(storedInput, 10) + parseFloat(inputs, 10)
        ).toString();
        break;
      default:
        calculatedValue = inputs;
    }
    setHisotry((prevValue) => [...prevValue, calculatedValue]);
    console.log("History Stored: " + history);
    setInputs(calculatedValue);
    setStoredInput(calculatedValue);
    setStoredOperator("");
  }

  return (
    <div className="container-fluid container">
      <div className="row">
        <div className="col-sm padding-0">
          <input
            className="history-display form-control"
            type="text"
            value={history}
            disabled
          />
        </div>
      </div>
      <div className="row">
        <div className="col-sm padding-0">
          <input
            className="input-calcdisplay form-control"
            type="text"
            value={inputs}
            disabled
          />
        </div>
      </div>
      <div className="row">
        <div className="col-xs-9 padding-0" >
          <button
            type="button"

            className="btn btn-secondary btn-sizing"
            style={{ fontSize: '180%' }}
            onClick={clearInput}
          >
            CLEAR
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="  btn btn-secondary btn-sizing"
            value="<="
            style={{ fontSize: '180%' }}
            onClick={clearEntry}
          >
            CE
          </button>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            variant="outlined"
            color="secondary"
            className="btn btn-danger btn-sizing"
            value="7"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            7
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="  btn btn-danger  btn-sizing"
            value="8"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            8
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="  btn btn-danger btn-sizing"
            value="9"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            9
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="btn btn-success btn-sizing"
            value="/"
            style={{ fontSize: '180%' }}
            onClick={updateOperator}
          >
            /
          </button>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="  btn btn-danger btn-sizing"
            value="4"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            4
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            class="  btn btn-danger btn-sizing"
            value="5"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            5
          </button>
        </div>
        <div class="col-xs-3 padding-0">
          <button
            type="button"
            class="  btn btn-danger btn-sizing"
            value="6"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            6
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="btn btn-success btn-sizing"
            value="*"
            style={{ fontSize: '180%' }}
            onClick={updateOperator}
          >
            *
          </button>
        </div>
      </div>
      <div className="row">
        <div className="col-xs-3 padding-0">
          <button
            type="button"
            className="  btn btn-danger btn-sizing"
            value="1"
            style={{ fontSize: '180%' }}
            onClick={updateInput}
          >
            1
          </button>
        </div>
        <div className="col-xs-3 padding-0">
          <button
